function takeScreenShot(){var n=$("#mobileVersion").is(":checked"),t=$("#siteUrl").val();console.log(">> takeScreenShot -> url: "+t+" mobile: "+n);$("#screenshot").empty();$("#screenshot").empty().append('<img src="images/loading.gif" alt="screenshot">');$.ajax({type:"POST",url:"/Home/TakeScreenShot?url="+t+"&mobile="+n,data:null,success:function(){console.log("Success!");screenShotIntervalHandle=setInterval(loadImage,1e3)},error:function(n,t){console.log("error: "+t)}})}function loadImage(){console.log(">> loadImage");$('<img src="images/screenshot.png?'+(new Date).getTime()+'">').load(function(){$("#screenshot").empty();$(this).appendTo("#screenshot");clearInterval(screenShotIntervalHandle)})}var screenShotIntervalHandle;$(function(){$("#takeScreenShot").click(takeScreenShot)});